INSERT INTO `` (`QUESTIONS`,`ANSWER`,`LOD`,`TRANSLATION`,`FRUIT`,`CATEGORY`) VALUES ('And though I have the gift of prophecy, and understand all mysteries, and all knowledge; and though I have all faith, so that I could remove mountains, and have not charity, I am nothing.','1 Corinthians 13:2','Intermediate','KJV','Love','');
INSERT INTO `` (`QUESTIONS`,`ANSWER`,`LOD`,`TRANSLATION`,`FRUIT`,`CATEGORY`) VALUES ('But I say unto you, Love your enemies, bless them that curse you, do good to them that hate you, and pray for them which despitefully use you, and persecute you;','Matthew 5:44','Easy','KJV','Love','');
