INSERT INTO `` (`VERSE`,`SCRIPTURE`,`LOD`,`TRANSLATION`,`FRUIT`,`CATEGORY`) VALUES ('Proverbs 16:32','He that is slow to anger is better than the mighty; and he that ruleth his spirit than he that taketh a city.','Easy','KJV','Temperance','');
INSERT INTO `` (`VERSE`,`SCRIPTURE`,`LOD`,`TRANSLATION`,`FRUIT`,`CATEGORY`) VALUES ('Romans 8:13','For if ye live after the flesh, ye shall die: but if ye through the Spirit do mortify the deeds of the body, ye shall live.','Easy','KJV','Temperance','');
INSERT INTO `` (`VERSE`,`SCRIPTURE`,`LOD`,`TRANSLATION`,`FRUIT`,`CATEGORY`) VALUES ('Proverbs 25:28','He that hath no rule over his own spirit is like a city that is broken down, and without walls.','Easy','KJV','Temperance','');
INSERT INTO `` (`VERSE`,`SCRIPTURE`,`LOD`,`TRANSLATION`,`FRUIT`,`CATEGORY`) VALUES ('Romans 8:6','For to be carnally minded is death; but to be spiritually minded is life and peace.','Easy','KJV','Temperance','');
